mapper.update:

	SELECT BOOKMARK_COUNT
	FROM CONTEST
	WHERE CONTEST_NO = ${contestNo}

UPDATE CONTEST SET BOOKMARK_COUNT = ${bookmarkCount} + 1
WHERE
CONTEST_NO = ${contestNo}

mapper.delete:
DELETE FROM LIKE_CON
WHERE CONTEST_NO = ${contestNo}
AND MEMBER_NO = ${memberNo}

mapper.update:

	SELECT BOOKMARK_COUNT
	FROM CONTEST
	WHERE CONTEST_NO = ${contestNo}

UPDATE CONTEST SET BOOKMARK_COUNT = ${bookmarkCount} - 1
WHERE
CONTEST_NO = ${contestNo}
